INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72057, 0, 0, 'You will take my place on the eternal brazier.', 2, 14, 0, 100, 0, 0, 38971, 76160, 'Ordos Aggro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72057, 1, 0, 'My pain... is endless...', 2, 14, 0, 100, 0, 0, 38974, 76157, 'Ordos Intro 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72057, 2, 0, 'The flames... never cease...', 2, 14, 0, 100, 0, 0, 38975, 76158, 'Ordos Intro 3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72057, 3, 0, 'I burn... for all time...', 2, 14, 0, 100, 0, 0, 38976, 76159, 'Ordos Intro 4');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72057, 4, 0, 'The eternal fire will never be extinguished.', 2, 14, 0, 100, 0, 0, 38972, 76166, 'Ordos Death');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72057, 5, 0, 'Up in smoke.', 2, 14, 0, 100, 0, 0, 38977, 76165, 'Ordos Kill');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72057, 6, 0, 'You will feel but a fraction of my agony.', 2, 14, 0, 100, 0, 0, 38980, 76163, 'Ordos - Pool of Fire');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72057, 7, 0, 'Your flesh will melt.', 2, 14, 0, 100, 0, 0, 38978, 76161, 'Ordos - Ancient Flame');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72057, 8, 0, 'Burn!', 2, 14, 0, 100, 0, 0, 38981, 76164, 'Ordos - Burning Soul');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72057, 9, 0, 'Your pain will be endless.', 2, 14, 0, 100, 0, 0, 38979, 76162, 'Ordos - Eternal Agony');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72145, 0, 0, 'I will NOT burn like the rest! Face the demons you fear, traitors!', 0, 14, 0, 100, 0, 0, 0, 74817, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72151, 0, 0, 'Almost... got... it...', 0, 12, 0, 100, 0, 0, 0, 74603, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72151, 1, 0, 'Well that was unexpected!', 0, 12, 0, 100, 0, 0, 0, 74604, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72191, 0, 0, 'Our enemies have finally arrived!  Demolishers, fire at will!', 0, 14, 0, 100, 0, 0, 38287, 74795, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72192, 0, 0, 'Hrnngg... thank you...', 0, 12, 0, 100, 0, 0, 38276, 74937, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72192, 1, 0, 'I may be bruised and beaten, but the hatred boils inside me.', 0, 12, 0, 100, 396, 0, 38277, 74938, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72192, 2, 0, 'Many times have I fallen at the hands of an Orc, and many times have I risen again.', 0, 12, 0, 100, 396, 0, 38278, 74939, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72192, 3, 0, 'But never again.', 0, 12, 0, 100, 274, 0, 38279, 74940, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72192, 4, 0, 'For every orc that struck me, I will cleave a thousand of their skulls!', 0, 12, 0, 100, 5, 0, 38280, 74941, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72192, 5, 0, 'This ends here!', 0, 14, 0, 100, 15, 0, 38281, 74942, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72192, 6, 0, 'I, Gamon, will save us!', 0, 14, 0, 100, 71, 0, 38282, 74943, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72192, 7, 0, 'Taste my axe!', 0, 14, 0, 100, 0, 0, 38271, 74944, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72192, 8, 0, 'Pay for your crimes!', 0, 14, 0, 100, 0, 0, 38272, 74945, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72192, 9, 0, 'I am a storm of pain!', 0, 14, 0, 100, 0, 0, 38283, 74946, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72192, 10, 0, 'You are an honorable Orc, Nazgrim. I regret what I must do.', 0, 12, 0, 100, 0, 0, 38274, 75887, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72192, 11, 0, 'You fought with honor. Gamon will ensure that your legacy lives on. For the Horde, my friend.', 0, 12, 0, 100, 0, 0, 38275, 75888, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72218, 0, 0, 'Hello. For this trial, you must keep us alive while we face the incoming illusions. I am Oto, and will protect you.', 0, 14, 0, 100, 0, 0, 38599, 75861, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72218, 1, 0, 'Illusionary Hive-Singers! Their sonic waves will harm us all.', 0, 14, 0, 100, 0, 0, 38596, 75864, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72218, 2, 0, 'I am a wall of iron, but I cannot hold much longer!', 0, 14, 0, 100, 0, 0, 38594, 75863, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72218, 3, 0, 'Ah, a new challenge, Illusionary Conquerors! When they become enraged, they strike with incredible power.', 0, 14, 0, 100, 0, 0, 38597, 75865, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72219, 0, 0, '', 0, 14, 0, 100, 0, 0, 38584, 75875, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72219, 1, 0, '', 0, 14, 0, 100, 0, 0, 38585, 75876, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72219, 2, 0, '', 0, 14, 0, 100, 0, 0, 38588, 75879, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72219, 3, 0, '', 0, 41, 0, 100, 0, 0, 0, 75595, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72220, 0, 0, 'Hah ha! Wonderful job, everyone! Friend, see Rotun about beginning the next trial.', 0, 14, 0, 100, 0, 0, 38970, 75885, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72220, 1, 0, 'Ahh, Illusionary Tunnelers! Speedy and I have faced these foes before. Beware, they may burrow into the ground and then suddenly pop up under a new target, and CHOMP! Hah ha hah ha.', 0, 14, 0, 100, 0, 0, 38967, 75882, '');
