INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72311, 0, 0, 'Well done! Landing parties, form up! Footmen to the front!', 0, 12, 0, 100, 25, 0, 38953, 75223, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72311, 1, 0, 'Yes. My scouts learned all about this one."Warlord Zaela." Bring her proto-drake down and the rest of the Dragonmaw will crumble.', 0, 12, 0, 100, 1, 0, 38954, 75224, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72311, 2, 0, 'Commandeer those towers! Turn the anti-air batteries around and blast her from the sky!', 0, 12, 0, 100, 25, 0, 38955, 75225, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72311, 3, 0, 'Some of you will need to stay behind to hold the beachhead.', 0, 12, 0, 100, 1, 0, 38170, 75248, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72311, 4, 0, 'That\'s both of them!  Fire the cannons!', 0, 14, 0, 100, 22, 0, 38944, 75231, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72311, 5, 0, 'Ha!  The bigger they are...', 0, 12, 0, 100, 153, 0, 38946, 75233, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72311, 6, 0, 'We will see if he has managed to keep up his end of the bargain.', 0, 12, 0, 100, 1, 0, 38947, 75234, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72356, 0, 0, '', 0, 14, 0, 100, 5, 0, 38740, 75276, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72356, 1, 0, 'I die... with honor... Lok\'tar Ogar...', 0, 14, 0, 100, 0, 0, 38193, 75926, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72357, 0, 0, 'You dare try to take my tower?  Hahaha...', 0, 14, 0, 100, 574, 0, 38737, 75279, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72357, 1, 0, 'You... cannot win... this war...', 0, 14, 0, 100, 0, 0, 38738, 75281, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72432, 0, 0, 'The Warchief ignored my council. I discovered he was building a compound beneath the caverns of Ragefire.', 0, 12, 0, 100, 0, 0, 38564, 74822, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72432, 1, 0, 'Thrall and Saurfang have already gone down below.', 0, 12, 0, 100, 0, 0, 38565, 74823, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72432, 2, 0, 'Go with caution.  Hellscream has been assembling an arsenal down there - goblin mercenaries, pandaren artifacts, and some sort of darkness I can feel in my very soul.', 0, 12, 0, 100, 0, 0, 38567, 74824, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72438, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 76988, '');
