INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72455, 0, 0, 'Have anything to say for yourself, Pandaren?', 0, 14, 0, 100, 1, 0, 39051, 74921, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72455, 1, 0, 'Do you see this burning candle? You have until it burns out to tell us about the Pandaren artifacts! Now start talking!', 0, 14, 0, 100, 397, 0, 39052, 74922, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72492, 0, 0, '', 0, 12, 0, 100, 5, 0, 39044, 74916, '');
