INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72560, 0, 0, 'Well done. The first brigade has made landfall.', 0, 12, 0, 100, 1, 0, 38167, 75245, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72560, 1, 0, 'Strategy, Dark Lady. We should repurpose the shore defenses.', 0, 12, 0, 100, 1, 0, 38168, 75246, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72560, 2, 0, 'Heroes, take the towers, turn the guns onto that proto-drake, and blast it from the sky!', 0, 12, 0, 100, 5, 0, 38169, 75247, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72560, 3, 0, 'Some of you should stay behind to hold the landing zone.', 0, 12, 0, 100, 1, 0, 38956, 75226, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72560, 4, 0, 'She seems to be assaulting her own towers!', 0, 14, 0, 100, 5, 0, 38152, 75252, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72560, 5, 0, 'We are halfway there!', 0, 14, 0, 100, 5, 0, 38153, 75253, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72560, 6, 0, 'Sometimes I impress even myself. And down goes the dragon!', 0, 14, 0, 100, 6, 0, 38154, 77004, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72560, 7, 0, 'Finally!  I will never get all of this dust out of my robes.', 0, 14, 0, 100, 5, 0, 38155, 75254, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72560, 8, 0, 'I will take that as a compliment.  We\'ll hold the docks for now and marshall the forces. Vol\'jin should await you at the gates of Orgrimmar.', 0, 14, 0, 100, 1, 0, 38156, 75255, '');
