INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 1, 0, 'Unfinished weapons begin to roll out on the assembly line.', 2, 41, 0, 100, 0, 0, 0, 74677, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 2, 0, 'You son of a...', 0, 14, 0, 100, 0, 0, 38450, 75818, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 3, 0, 'The SD-5 Automated Shredder defense unit. A labor of love.', 0, 14, 0, 100, 0, 0, 38457, 74669, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 4, 0, 'An Automated Shredder draws near!', 2, 41, 0, 100, 0, 0, 0, 74827, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 5, 0, 'My legacy... will live on...', 0, 14, 0, 100, 0, 0, 38448, 75819, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 6, 0, 'Goro\'dan! You\'re on deck!', 2, 14, 0, 100, 0, 0, 38449, 74654, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 7, 0, 'Alright, Shanna. You\'re up.', 2, 14, 0, 100, 0, 0, 38452, 74657, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 8, 0, 'Looks like I\'m in the market for a new assistant.', 2, 14, 0, 100, 0, 0, 38453, 74659, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 9, 0, 'That\'s for the Juggernaut.', 0, 14, 0, 100, 0, 0, 38463, 74660, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 9, 1, 'Another satisfied customer.', 0, 14, 0, 100, 0, 0, 38464, 74661, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 10, 0, 'Presenting... the beautiful new ST-03 Shockwave missile turret!', 0, 14, 0, 100, 0, 0, 38471, 74663, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 11, 0, 'What\'s better than a bomb, huh?! I\'ll tell ya - a bomb with two bombs inside!', 0, 14, 0, 100, 0, 0, 38469, 74664, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 12, 0, 'Meet the LT-403 Evaporation laser. A personal point of pride.', 0, 14, 0, 100, 0, 0, 38467, 74665, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72694, 13, 0, 'Ya\' ever wonder how magnets work?', 0, 14, 0, 100, 0, 0, 38468, 74666, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72726, 0, 0, 'Ah, new customers. Welcome!', 0, 14, 0, 100, 0, 0, 0, 75286, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72726, 1, 0, 'Leaving so soon? Do come again!', 0, 14, 0, 100, 0, 0, 0, 75289, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72726, 2, 0, 'Time to close already? Well, I guess I should get home to Chao-Wei.', 0, 14, 0, 100, 0, 0, 0, 75291, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72726, 3, 0, 'The secret to good noodle soup begins with strong, thick noodles!', 0, 14, 0, 100, 0, 0, 0, 75287, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72726, 4, 0, 'The soup must be piping hot to warm the soul!', 0, 14, 0, 100, 0, 0, 0, 75288, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72779, 0, 0, '*Cough cough* Nyyyunnngggg...', 0, 12, 0, 100, 0, 0, 38307, 74997, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72779, 1, 0, 'Cho... the outsiders... THEY did this... we should never have let them in...', 0, 12, 0, 100, 0, 0, 38308, 74998, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72872, 0, 0, 'Astounding! Another chamber that looks to originate from these "Titans."', 0, 12, 0, 100, 0, 0, 38138, 74987, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72872, 1, 0, 'It must have been hidden away long before recorded history.  And with good reason!', 0, 12, 0, 100, 0, 0, 38139, 74988, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72872, 2, 0, 'In the chamber ahead, under our peaceful land, slept the heart of an Old God.', 0, 12, 0, 100, 0, 0, 38140, 74989, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72872, 3, 0, 'Oh my!  What is this?  Hello!  I am Lorewalker Cho!', 0, 12, 0, 100, 3, 0, 38141, 74990, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72872, 4, 0, 'Further corruption?  No, we are here to stop the corruption, and save Pandaria!', 0, 12, 0, 100, 274, 0, 38142, 74991, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72872, 5, 0, 'Yes! Please let us pass!', 0, 12, 0, 100, 273, 0, 38143, 74992, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72872, 6, 0, 'Taran Zhu!', 0, 14, 0, 100, 0, 0, 38144, 74993, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72872, 7, 0, 'Don\'t speak, friend, I will take you to the healers.', 0, 12, 0, 100, 0, 0, 38145, 74994, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72872, 8, 0, 'I will find help!  Try to hold off the Sha for as long as you can!', 0, 14, 0, 100, 0, 0, 38146, 74995, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72916, 0, 0, 'Wow, I have quite an appetite today!', 0, 12, 0, 100, 0, 0, 0, 75394, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72916, 1, 0, 'I heard they discovered the lost secret of the Lin family\'s secret ingredient noodle soup.', 0, 12, 0, 100, 0, 0, 0, 75356, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72964, 0, 0, 'You\'re Screwed!', 2, 14, 0, 100, 0, 0, 0, 77113, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72970, 0, 0, 'The eons have awakened me.', 0, 14, 0, 100, 0, 0, 0, 75695, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (72994, 0, 0, 'Be careful. I would not want you to fall into the arena.', 0, 12, 0, 100, 0, 0, 0, 75391, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73037, 0, 0, 'Please, come down. How am I ever going to explain this to the rest of the Tillers?', 0, 12, 0, 100, 0, 0, 0, 75608, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73037, 0, 1, 'If you fall off, I\'m not going in after you!', 0, 12, 0, 100, 0, 0, 0, 75607, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73303, 0, 0, 'Long ago, the Jade Serpent instructed me to purify my spirit and become one with the land, my doubts were vanquished.', 0, 14, 0, 100, 0, 0, 38036, 75933, 'Emperor Shaohao - Yulon Intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73303, 1, 0, 'When my journey began, I was reckless in my strength. The White Tiger taught me control. My hatred, anger, and violence were vanquished.', 0, 14, 0, 100, 0, 0, 38037, 75934, 'Emperor Shaohao - Xuen Intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73303, 2, 0, 'The Red Crane saw hope in me, and instructed me to look inward. Despite my visage of despair, I found this hope, and the despair was vanquished.', 0, 14, 0, 100, 0, 0, 38038, 75935, 'Emperor Shaohao - Chi-Ji');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73303, 3, 0, 'In the face of great fear, the Black Ox taught me fortitude. Through terror and darkness it persevered. The fear was vanquished.', 0, 14, 0, 100, 0, 0, 38039, 75936, 'Emperor Shaohao - Niuzao');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73303, 4, 0, 'You have walked the trial of wisdom, and learned of the path of the Jade Serpent. May you learn from this lesson, as I did.', 0, 14, 0, 100, 0, 0, 38040, 75937, 'Emperor Shaohao - Yulon Outro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73303, 5, 0, 'You have walked the trial of strength, and learned of the path of the White Tiger. May you remember this lesson, always.', 0, 14, 0, 100, 0, 0, 38041, 75938, 'Emperor Shaohao - Xuen Outro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73303, 6, 0, 'You have walked the trial of hope, and learned of the path of the Red Crane. May it guide your footsteps through time.', 0, 14, 0, 100, 0, 0, 38042, 75939, 'Emperor Shaohao - Red Crane Outro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73303, 7, 0, 'You have walked the trial of fortitude, and learned of the path of the Black Ox. May it bless your passage.', 0, 14, 0, 100, 0, 0, 38043, 75940, 'Emperor Shaohao - Niuzao Outro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73303, 8, 0, 'Welcome to a very special trial!  The wisdom of a leader is reflected in the greatness of his followers. Let us see if your training has truly inspired those you lead...', 0, 14, 0, 100, 0, 0, 38044, 75309, 'Emperor Shaohao - Intro');
