INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73316, 11, 0, '', 0, 12, 0, 100, 0, 0, 0, 75393, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73316, 12, 0, '', 0, 12, 0, 100, 4, 0, 0, 76050, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 0, 0, 'Ah, we adventure together again, except this time I am afraid that the circumstances are much more dire.', 0, 14, 0, 100, 378, 0, 38120, 74968, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 1, 0, 'The land is scarred, the ever-blooming trees and plants wither and die as the water from the pools drains away.', 0, 14, 0, 100, 378, 0, 38122, 74970, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 2, 0, 'Once, the Titans used these life-giving waters to create and shape all life in Pandaria.', 0, 14, 0, 100, 0, 0, 38124, 74972, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 3, 0, 'It is these waters that kept the Vale in bloom.  Their runoff into the Valley of the Four Winds created the most fertile farmland in the world!', 0, 14, 0, 100, 378, 0, 38125, 74973, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 4, 0, 'And now, the malignance of the Old God has manifested itself within the waters.', 0, 14, 0, 100, 1, 0, 38126, 74974, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 5, 0, 'Such a foul, foul thing - destroy it, before it seeps deep into the soil and corrupts all of Pandaria!', 0, 14, 0, 100, 5, 0, 38127, 74975, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 6, 0, 'Ah, you have done it!  The waters are pure once more.', 0, 14, 0, 100, 5, 0, 38128, 74976, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 7, 0, 'Can you feel their life-giving energies flow through you?', 0, 14, 0, 100, 6, 0, 38129, 74977, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 8, 0, 'It will take much time for the Vale to heal, but you have given us hope!', 0, 14, 0, 100, 1, 0, 38130, 74978, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 9, 0, 'Can it be...? Oh no, no no no no no.', 0, 14, 0, 100, 274, 0, 38131, 74979, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 10, 0, 'The Golden Lotus... they pledged their lives to defend this place.', 0, 14, 0, 100, 1, 0, 38132, 74980, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 11, 0, 'Yes!  Rook Stonetoe!  You remember me!  What has happened to you?', 0, 14, 0, 100, 5, 0, 38133, 74981, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 12, 0, 'I see. This sha energy has trapped their spirits here, to endlessly relive their failure. It feeds on their despair.', 0, 14, 0, 100, 1, 0, 38134, 74982, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 13, 0, 'This is a fate far worse than death - please, heroes!  Set their souls free!', 0, 14, 0, 100, 1, 0, 38135, 74984, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 14, 0, 'Be at peace, dear friends.', 0, 14, 0, 100, 1, 0, 38136, 74985, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73330, 15, 0, 'May your souls become one with the land you gave your life to protect.', 0, 14, 0, 100, 1, 0, 38137, 74986, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73342, 0, 0, 'Defend me!', 0, 14, 0, 100, 0, 0, 0, 76030, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73349, 0, 0, 'Our work is not yet complete! Stop them!', 0, 14, 0, 100, 0, 0, 0, 76003, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73435, 0, 0, 'Dare ta drink a Dragon P.I.E.! It\'ll put hair on yer chest, and then hair on THAT!', 0, 12, 0, 100, 4, 0, 0, 77080, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73435, 0, 1, 'GRUB N\' GROG!', 0, 12, 0, 100, 22, 0, 0, 77078, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73435, 0, 2, 'Ye won\'t find anythin\' like it elsewhere. Come and try!', 0, 12, 0, 100, 1, 0, 0, 77079, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73435, 0, 3, 'The perfect mix of northern and southern styles!', 0, 12, 0, 100, 5, 0, 0, 77077, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73483, 0, 0, 'It is not too late, Garrosh. Lay down the mantle of Warchief. We can end this here, now, with no more bloodshed.', 0, 14, 0, 100, 0, 0, 38934, 74698, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73483, 1, 0, 'Then you have forced my hand.  I will correct the mistake I made long ago.  Spirits of the wind, the earth, the water, hear my call!  Come to my aid!', 0, 14, 0, 100, 0, 0, 38935, 74699, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73483, 2, 0, 'Never powerless Garrosh. And never alone.', 0, 14, 0, 100, 0, 0, 38936, 74700, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73547, 0, 0, 'Hahahahaha!', 0, 14, 0, 100, 0, 0, 0, 76174, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73547, 1, 0, 'I live again!', 0, 14, 0, 100, 0, 0, 0, 76173, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73547, 2, 0, 'The South Seas are mine to pillage for all eternity!', 0, 14, 0, 100, 0, 0, 0, 76175, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73547, 3, 0, 'Come, Vazuvius, rise from the depths!', 0, 14, 0, 100, 0, 0, 0, 76176, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73547, 4, 0, 'Kill them all! No survivors!', 0, 14, 0, 100, 0, 0, 0, 76388, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73547, 5, 0, 'This cannot be! The Vazuvius could never fall to the likes of you!', 0, 14, 0, 100, 0, 0, 0, 76778, '');
