INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73605, 0, 0, 'Look here! He left his weapon behind.', 0, 14, 0, 100, 0, 0, 38158, 77040, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73605, 1, 0, 'This means he\'s completely unhinged.', 0, 12, 0, 100, 0, 0, 38159, 77041, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73605, 2, 0, 'Likewise. Sylvanas and I have both sent ships to support Vol\'jin\'s revolution.', 0, 12, 0, 100, 0, 0, 38160, 77042, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73605, 3, 0, 'It is always a pleasure to see you, Lady Proudmoore.', 0, 12, 0, 100, 0, 0, 38161, 77043, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73618, 0, 0, 'You there! Come and try some Greenstone Brew!', 0, 12, 0, 100, 25, 0, 0, 77103, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73618, 0, 1, 'Greenstone Brew! Straight from the heart of the Jade Forest.', 0, 12, 0, 100, 1, 0, 0, 77100, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73618, 0, 2, 'Nothing beats the bold bite of Greenstone Brew!', 0, 12, 0, 100, 4, 0, 0, 77102, '');
