INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73647, 0, 0, 'Exotic, aromatic, a brew good for both body and spirit!', 0, 12, 0, 100, 1, 0, 0, 77055, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73647, 0, 1, 'Brave the brew that\'s truly unique! Invigorating, satisfying... amber-brew!', 0, 12, 0, 100, 25, 0, 0, 77058, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73647, 0, 2, 'Exquisite amber-brew from beyond the Serpent\'s Spine!', 0, 12, 0, 100, 22, 0, 0, 77057, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73651, 0, 0, 'It\'s Stormstout time!', 0, 12, 0, 100, 22, 0, 0, 77092, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73651, 0, 1, 'Stormstout. Probably the best brew in Azeroth.', 0, 12, 0, 100, 273, 0, 0, 77099, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73651, 0, 2, 'When you say Stormstout, you\'ve said it all.', 0, 12, 0, 100, 1, 0, 0, 77093, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73651, 0, 3, 'If you\'ve got the time, we\'ve got the brew. Stormstout!', 0, 12, 0, 100, 6, 0, 0, 77097, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73651, 0, 4, 'Stormstout. Pandaren for "beer."', 0, 12, 0, 100, 25, 0, 0, 77098, '');
