INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73657, 0, 2, '', 0, 12, 0, 100, 396, 0, 0, 77068, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73666, 0, 0, 'Your challenge has been answered, outsider. Ordon, claim this area in the name of the Burning God!', 0, 14, 0, 100, 0, 0, 0, 76582, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73716, 0, 0, 'Ah, more of Vol\'jin\'s revolutionaries. You\'ve made it.', 0, 12, 0, 100, 0, 0, 38920, 76677, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73716, 1, 0, 'Have you found Thrall? I am badly hurt, he insisted on going ahead alone.', 0, 12, 0, 100, 0, 0, 38921, 76678, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73716, 2, 0, 'Tell me, how goes the battle up above? What of Nazgrim? Speak to me!', 0, 12, 0, 100, 0, 0, 38922, 76679, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73716, 3, 0, 'Ah, Nazgrim. A great leader and a fine warrior. He valued his oath to the Warchief more than his life.', 0, 12, 0, 100, 0, 0, 38923, 76680, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73716, 4, 0, 'I tried to tell him... to tell him that Hellscream betrayed us, cast aside a Warchief\'s responsibility to his own people.  But Nazgrim... too loyal... too proud.', 0, 12, 0, 100, 0, 0, 38924, 76681, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73716, 5, 0, 'Damn Hellscream.  His ambition tore our Horde apart.', 0, 12, 0, 100, 274, 0, 38925, 76682, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73716, 6, 0, 'Go on, find Thrall.  Finish this.  I will live.', 0, 12, 0, 100, 1, 0, 38926, 76683, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73812, 0, 0, 'Steaming goat noodle: NUMBER ONE!', 0, 12, 0, 100, 51, 0, 0, 77060, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73812, 0, 1, 'Come fill belly with noodle, yes! Very good!', 0, 12, 0, 100, 1, 0, 0, 77061, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73812, 0, 2, 'Noodle so tasty! You come try, yes?', 0, 12, 0, 100, 1, 0, 0, 77063, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73813, 0, 0, 'Please try my Harmonious River Noodles. They will not disappoint.', 0, 12, 0, 100, 2, 0, 0, 77089, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73813, 0, 1, 'Fresh fish, caught within the hour! ...I think? Curse this island.', 0, 12, 0, 100, 1, 0, 0, 77090, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73813, 0, 2, 'River style noodles, made with only the freshest fish.', 0, 12, 0, 100, 3, 0, 0, 77088, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73813, 0, 3, 'Harmonious River Noodles!', 0, 12, 0, 100, 22, 0, 0, 77091, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73814, 0, 0, 'Monmon study MANY weeks with master noodle chef.', 0, 12, 0, 100, 1, 0, 0, 77034, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73814, 0, 1, 'I got good noodles here!', 0, 12, 0, 100, 3, 0, 0, 77032, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73814, 0, 2, 'BUY NOODLES!', 0, 12, 0, 100, 4, 0, 0, 77030, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73815, 0, 0, 'HOT AND HEARTY! Come try Yaungolian noodles!', 0, 12, 0, 100, 5, 0, 0, 77053, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73815, 0, 1, 'Bold flavor, bold heat! No other noodles come close!', 0, 12, 0, 100, 1, 0, 0, 77054, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73815, 0, 2, 'Why settle for weak, flavorless noodles? Try them yaungolian style!', 0, 12, 0, 100, 6, 0, 0, 77052, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73816, 0, 0, 'You will find only the best noodles in all Pandaria here.', 0, 12, 0, 100, 1, 0, 0, 77037, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73816, 0, 1, 'Please come enjoy my fine Golden Dragon Noodles.', 0, 12, 0, 100, 2, 0, 0, 77036, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73816, 0, 2, 'Golden Dragon Noodles! The very BEST!', 0, 12, 0, 100, 22, 0, 0, 77039, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73817, 0, 0, 'So tasty! So delicious! Double Lucky special noodles!', 0, 12, 0, 100, 396, 0, 0, 77084, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73817, 0, 1, 'Hey hey! Come here, have some tasty noodles!', 0, 12, 0, 100, 3, 0, 0, 77085, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73817, 0, 2, 'Yoo-hoo! Come try some Lucky Mushroom Noodles?', 0, 12, 0, 100, 24, 0, 0, 77083, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73817, 0, 3, 'Lucky-Lucky! Come have some noodles!', 0, 12, 0, 100, 22, 0, 0, 77082, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73859, 0, 0, 'Send in da reserves! Charge!', 0, 14, 0, 100, 0, 0, 0, 76899, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73909, 0, 0, 'I\'m summoning a Flamestrike, lead them into it!', 0, 14, 0, 100, 0, 0, 0, 75256, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73939, 0, 0, 'Don\'t be foolish, Vol\'jin! You have no siege weapons left! You cannot win this battle!', 0, 14, 0, 100, 5, 0, 38205, 75920, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73939, 1, 0, 'You may have defeated my war machine, but the gate still stands. You will not take Orgrimmar today.', 0, 14, 0, 100, 274, 0, 38206, 75921, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73939, 2, 0, 'What? Kor\'kron, to the gates! Now!', 0, 14, 0, 100, 397, 0, 38207, 75922, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73941, 0, 0, 'Our casualties are too heavy!', 0, 14, 0, 100, 5, 0, 38262, 76623, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73941, 1, 0, 'Perhaps our newfound reinforcements will aid us.', 0, 12, 0, 100, 1, 0, 38263, 76624, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73941, 2, 0, 'Perhaps there is hope after all.', 0, 12, 0, 100, 1, 0, 38264, 76625, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73941, 3, 0, 'Blessings of the Earth Mother upon you.', 0, 14, 1, 100, 0, 0, 0, 6724, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73942, 0, 0, 'Ain\'t no other way, mon. We gotta stop Garrosh here an\' now, else we be runnin\' the rest of our lives.', 0, 12, 0, 100, 1, 0, 39151, 76610, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73942, 1, 0, 'Dey be heroes, no doubt about dat.', 0, 14, 0, 100, 1, 0, 39144, 76612, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73942, 2, 0, 'Da beast be slain!', 0, 14, 0, 100, 5, 0, 39145, 76613, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73942, 3, 0, 'Tyrande...', 0, 12, 0, 100, 1, 0, 39153, 76614, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (73962, 0, 0, '', 0, 14, 0, 100, 5, 0, 38309, 76620, '');
